<script>
import {gsap} from "gsap";
import {MotionPathPlugin} from "gsap/MotionPathPlugin"

export default {
  name: "Navigation",
  props:{
    currentIndex: Number
  },
  data() {
    return {
      boxes: null,
      names: null,
      tl: []
    }
  },
  mounted() {
    gsap.registerPlugin(MotionPathPlugin);
    setTimeout(()=>{
      this.boxes = Array.prototype.slice.call(document.getElementsByClassName('navigation-box'));
      this.names = document.getElementById('navigation-names');
      this.names.classList.add('animate__fadeOut');

      this.boxes.forEach((e)=>{
        this.tl.push(gsap.timeline({paused: true})
            .to( e, {
              motionPath: {
                path: e.nextElementSibling,
                align: e.nextElementSibling,
                autoRotate: true,
                alignOrigin: [0.5, 0.5]
              },
              immediateRender: true,
              duration: 0.35,
              scale: 1.5
            }))
      });
    },1250); // wait for animation to finish before trying to find dom elements
  },
  methods: {
    change(ind) {
      this.$emit('change', ind);
    },
    onHover() {
      this.tl.forEach((e)=>{
        e.play()
      });
      this.names.classList.remove('animate__fadeOut');
      this.names.classList.add('animate__fadeIn');
    },
    onMouseOut() {
      this.tl.forEach((e)=>{
        e.reverse()
      });
      this.names.classList.remove('animate__fadeIn');
      this.names.classList.add('animate__fadeOut');
    },
    toggleMenu() {
      this.tl.forEach((e)=>{
        if(e.reversed() || (!e.reversed() && this.names.classList.contains('animate__fadeOut'))){
          e.play()
        } else {
          e.reverse()
        }
      });
      this.names.classList.toggle('animate__fadeIn');
      this.names.classList.toggle('animate__fadeOut');
    }
  }
}
</script>